# Get the contract instances
gold_contract = web3.eth.contract(address=Web3.toChecksumAddress(gold_contract_address), abi=gold_contract_abi)
bridge_contract = web3.eth.contract(address=bridge_contract_address, abi=bridge_abi)

destination_chain_id = 150 #Klaytn Mainnet

# Запись хэшей в файл result.txt
def write_hash_to_file(tx_hash):
    with open('result.txt', 'a') as file:
        file.write(f'{tx_hash}\n')

# Read private keys from file
with open('private_keys.txt', 'r') as f:
    private_keys = [line.strip() for line in f]

print("АПРУВ АТАКА ЗАПУЩЕНА")

for private_key in private_keys:
    # Get account address from private key
    account_address = web3.eth.account.privateKeyToAccount(private_key).address

    # Get Gold balance
    balance = gold_contract.functions.balanceOf(account_address).call()

    if balance > 0:
        # Выполнение функции approve()
        nonce = web3.eth.getTransactionCount(account_address)
        gas_estimate = None
        gas_price = 32032449048
        print("Цена газа:", web3.eth.gasPrice)

        try:
            # Ваш код, вызывающий ошибку ContractLogicError
            gas_estimate = gold_contract.functions.approve(bridge_contract_address, balance).estimateGas({
                'from': account_address,
                'gasPrice': gas_price,
            })
        except ContractLogicError as e:
            print("Ошибка выполнения контракта:", e)

        approve_tx = gold_contract.functions.approve(bridge_contract_address, balance).buildTransaction({
            'from': account_address,
            'gas': gas_estimate,
            'gasPrice': gas_price,
            'nonce': nonce,
        })


        # Sign the transaction
        signed_approve_txn = web3.eth.account.signTransaction(approve_tx, private_key=private_key)

        # Send the transaction
        tx_hash = web3.eth.sendRawTransaction(signed_approve_txn.rawTransaction)

        print(f'Транзакция approve отправлена: {web3.toHex(tx_hash)}')

        # Ждем от 20 до 30 секунд
        time.sleep(random.randint(20, 30))

        # Wait for the transaction to be mined
        web3.eth.waitForTransactionReceipt(tx_hash)

print("АПРУВ АТАКА ЗАВЕРШЕНА")