# Get the contract instances
synapse_bridge_contract = web3.eth.contract(address=synapse_bridge_contract_address, abi=synapse_bridge_abi)


# Read private keys from file
with open('private_keys.txt', 'r') as f:
    private_keys = [line.strip() for line in f]

print("СИБИЛ АТАКА ЗАПУЩЕНА")

for private_key in private_keys:
    # Get account address from private key
    account_address = web3.eth.account.privateKeyToAccount(private_key).address

    gas_price = web3.eth.gasPrice * 2
    gas_estimate = None
    print("Цена газа:", gas_price)
    amount_in = web3.toWei(0.1, 'ether')
    amount_out_min = int(300)
    print(amount_out_min)
    
    try:
        # Ваш код, вызывающий ошибку ContractLogicError
        gas_estimate = synapse_bridge_contract.functions.swapExactETHForTokens(amount_out_min,
                                                                               [wJewel_address, gold_address],
                                                                               account_address, int(time.time()) + 600).estimateGas({
            'from': account_address,
            'value': amount_in,
            'gasPrice': 40032449048
        })
    except ContractLogicError as e:
        print("Ошибка выполнения контракта:", e)

    nonce = web3.eth.getTransactionCount(account_address)
    # Build the deposit transaction
    deposit_txn = synapse_bridge_contract.functions.swapExactETHForTokens(amount_out_min,
                                                                          [wJewel_address, gold_address],
                                                                          account_address, int(time.time()) + 600).buildTransaction({
        'from': account_address,
        'value': amount_in,
        'gas': gas_estimate,
        'gasPrice': 40032449048,
        'nonce': nonce
    })

    # Sign the transaction
    signed_deposit_txn = web3.eth.account.signTransaction(deposit_txn, private_key=private_key)

    # Send the transaction
    tx_hash = web3.eth.sendRawTransaction(signed_deposit_txn.rawTransaction)

    print(f"Succesfully swapped {web3.fromWei(0.1, 'ether')} Jewel for Gold on {account_address}")

    # Random delay between 30 and 60 seconds
    time.sleep(random.randint(20, 40))

print("СИБИЛ АТАКА ЗАВЕРШЕНА")